import './index.css';
import { useState, useEffect } from "react"
import Header from './components/Header';
import GroupedTeamMempers from './components/GroupedTeamMempers';
import Nav from './components/Nav';
import NotFound from './components/NotFound';
import Employees from './components/Employees';
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom'

function App() {
  const [team, setTeam] = useState(JSON.parse(localStorage.getItem('teamList')) || 'TeamB')
  const [employees, setEmployees] = useState(JSON.parse(localStorage.getItem('employeesList')) ||[
    {
      id: 1,
      fullName: "Bob Jones",
      designation: "JavaScript Developer",
      gender: "male",
      teamName: "TeamA"
    },
    {
      id: 2,
      fullName: "Jill Bailey",
      designation: "Node Developer",
      gender: "female",
      teamName: "TeamA"
    },
    {
      id: 3,
      fullName: "Gail Shepherd",
      designation: "Java Developer",
      gender: "female",
      teamName: "TeamA"
    },
    {
      id: 4,
      fullName: "Sam Reynolds",
      designation: "React Developer",
      gender: "male",
      teamName: "TeamB"
    },
    {
      id: 5,
      fullName: "David Henry",
      designation: "DotNet Developer",
      gender: "male",
      teamName: "TeamB"
    },
    {
      id: 6,
      fullName: "Sarah Blake",
      designation: "SQL Server DBA",
      gender: "female",
      teamName: "TeamB"
    },
    {
      id: 7,
      fullName: "James Bennet",
      designation: "Angular Developer",
      gender: "male",
      teamName: "TeamC"
    },
    {
      id: 8,
      fullName: "Jessica Faye",
      designation: "API Developer",
      gender: "female",
      teamName: "TeamC"
    },
    {
      id: 9,
      fullName: "Lita Stone",
      designation: "C++ Developer",
      gender: "female",
      teamName: "TeamC"
    },
    {
      id: 10,
      fullName: "Daniel Young",
      designation: "Python Developer",
      gender: "male",
      teamName: "TeamD"
    },
    {
      id: 11,
      fullName: "Adrian Jacobs",
      designation: "Vue Developer",
      gender: "male",
      teamName: "TeamD"
    },
    {
      id: 12,
      fullName: "Devin Monroe",
      designation: "Graphic Designer",
      gender: "male",
      teamName: "TeamD"
    }])
  useEffect(()=> {
    localStorage.setItem('employeesList', JSON.stringify(employees))
  },[employees])
  useEffect(()=> {
    localStorage.setItem('teamList', JSON.stringify(team))
  },[team])
  
  const selValCh = (e) => {
    setTeam(e.target.value)
  }

  const hangleChangingTeam = (event) => {
    const newthing = employees.map((all) => all.id == event.currentTarget.id ? (all.teamName == team) ? {...all, teamName: ''} : {...all, teamName: team} : all)
    setEmployees(newthing)
  }
  return (
      <Router>
        <Nav />
        <Routes>
        
          <Route path='/' element={
            <>
            
            <Header counter={employees.filter(a => a.teamName == team).length} />
            <Employees selValCh={selValCh}
            hangleChangingTeam={hangleChangingTeam}
            team={team}
            employees={employees}/>
            </>
          }>
          </Route>
          <Route path='/GroupedTeamMempers' element={<GroupedTeamMempers />}>
          </Route>
          <Route path='/GroupedTeamMempers/fr' element={<GroupedTeamMempersfr />}>
          </Route>
          <Route path='*' element={
          <>           
          <NotFound />
          </>
          }>
          </Route>
        </Routes>
      </Router>
  );
}

export default App;
