import { useState, useEffect } from "react"
import femaleProfile from "../femaleProfile.jpg"
import maleProfile from "../maleProfile.jpg"

const Employees = () => {

  const [employees, setEmployees] = useState([])

  useEffect(()=>{
    fetch('http://localhost:5000/tasks')
      .then(req => req.json())
      .then(data => setEmployees(data))
  },[]) 

  const [team, setTeam] = useState('TeamB')
  const selValCh = (e) => {
    setTeam(e.target.value)
  }

  const hangleChangingTeam = async (id) => {
    const one = await fetch(`http://localhost:5000/tasks/${id}`)
    const two = await one.json()
    const after = {...two, teamName: team} 

    const req = await fetch(`http://localhost:5000/tasks/${id}`, {
      method: 'PUT',
      headers: {'content-type' : 'application/json'},
      body: JSON.stringify(after)
    })

    setEmployees(employees.map(all => all.id == id ? after : all ))
  }

  return (
    <main className="container ">
      <div className="row mt-3 mb-3 justify-content-center">
        <div className="col-6">
          <select value={team} className="form-select form-select-lg" onChange={selValCh}>
            <option value="TeamA">TeamA</option>
            <option value="TeamB">TeamB</option>
            <option value="TeamC">TeamC</option>
            <option value="TeamD">TeamD</option>
          </select>
        </div>
      </div>
      <div className="row mt-3 mb-3 justify-content-center">
        <div className="col-8">
          <div className="card-collection">
            {employees.map(person => (
              <div key={person.id} id={person.id} className={`card m-2 ${person.teamName == team ? "him" : ""}`} style={{ cursor:"pointer"}} onClick={() => hangleChangingTeam(person.id)}>
                <img className="card-img-top" src={person.gender == "male" ? maleProfile:femaleProfile}/>
                <div className="card-body">
                  <h1 className="card-title" >{person.fullName}</h1>
                  <p className="card-text" >{person.designation}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </main>
  )
}

export default Employees
