import Tasks from './components/Tasks';
import { useState } from "react"
import '../src/index.css';

function App() {
  const form = document.querySelector('.form')
  const resu = [
    {name:"ali galal",
    id:2775,
    resu:"70%"},
    {name:"ahmed khalil",
    id:2776,
    resu:"60%"},
    {name:"sobhy fawzi",
    id:3030,
    resu:"98%"},
  ]
  const [vars, tastStat] = useState([
    {id:1}
    ])
  const [finalresu, resufinal] = useState([
    {name:"sobhy fawzi",
    id:3030,
    resu:"98%"}
    ])
  form.addEventListener('submit', e =>{
    e.preventDefault()
    const formData = new FormData(form);
    const seatNum = formData.get('seatNum');

    const updatedVars = [vars];
    updatedVars[0].id = seatNum;
    tastStat(updatedVars);

    const loo = resu.filter(stu => stu.id == seatNum)

    const updatedFinalresu = [finalresu];
    updatedFinalresu[0].name = loo[0].name;
    updatedFinalresu[0].id = seatNum;
    updatedFinalresu[0].resu = loo[0].resu;
    resufinal(updatedFinalresu);
  })
  
    return (
      <div className='container'>
        <Tasks vasrs={vars} finalresu={finalresu}/>
      </div>
    );
}

export default App;
