import Tasks from './components/Tasks';
import { useState } from "react"
import '../src/index.css';

function App() {
  const form = document.querySelector('.form')
  const resu = [
    {name:"ali galal",
    id:2775,
    resu:"70%"},
    {name:"ahmed khalil",
    id:2776,
    resu:"60%"},
    {name:"sobhy fawzi",
    id:3030,
    resu:"98%"},
  ]

  const [finalresu, resufinal] = useState([
    {name:"",
    id:'',
    resu:""}
    ])
  form.addEventListener('submit', e =>{
    e.preventDefault()
    const formData = new FormData(form);
    const seatNum = formData.get('seatNum');

    const loo = resu.filter(stu => stu.id == seatNum)


    resufinal(loo);
  })
  
    return (
      <div className='container'>
        <Tasks finalresu={finalresu}/>
      </div>
    );
}

export default App;
