import Header from './components/Header';
import Footer from './components/Footer';
import Tasks from './components/Tasks';
import About from './components/About';
import AddTask from './components/AddTask';
import { useState, useEffect } from "react"
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom'
import '../src/index.css';

function App() {
  const [ShowAddTask, setShowAddTask] = useState(true)
  const [vars, tastStat] = useState([])

  useEffect(()=>{
    const getTasks = async () => {
      const tasksFromServer = await fetchApi()
      tastStat(tasksFromServer)
    }
    getTasks()
  },[])

  const fetchApi = async () => {
    const req = await fetch('http://localhost:5000/tasks')
    const data = await req.json()
    return data
  }
  const fetchApione = async (id) => {
    const req = await fetch(`http://localhost:5000/tasks/${id}`)
    const data = await req.json()
    return data
  }

  const addtask = async (task)=>{
    const reqw = await fetch('http://localhost:5000/tasks', {
      method: 'POST',
      headers: {"Content-type":"application/json"},
      body: JSON.stringify(task)
    })
    
    const looop = await reqw.json()
    console.log(looop)
    tastStat([...vars, looop])
  }

  const deleteTask = async (id) =>{
    await fetch(`http://localhost:5000/tasks/${id}`,{
      method: 'DELETE'
    })

    tastStat(vars.filter(ee => ee.id != id))
  }
  const toggleReminder = async (id, classNamee) =>{
    const eee = await fetchApione(id)
    const newdata = {...eee, reminder: !eee.reminder}
    const newfench = await fetch(`http://localhost:5000/tasks/${id}`, {
      method: 'PUT',
      headers: {'Content-type':'application/json'},
      body: JSON.stringify(newdata)
    })


    tastStat(vars.map(ee => ee.id == id ? {...ee, reminder: !ee.reminder} : ee))
  }
  return (
    <Router>
      <div className='container'>
        <Header onAdd={()=>setShowAddTask(!ShowAddTask)} ll={ShowAddTask} />
        
        <Route path='/' exact render={(props)=>{
          <>
            {ShowAddTask && <AddTask addtask={addtask}/>}
            <Tasks vasrs={vars} ondelete={deleteTask} toggleReminder={toggleReminder}/>
          </>
        }} />
        <Route path='/about' Component={About} />
        <Footer />
      </div>
    </Router>
  );
}

export default App;
