import { useState } from "react"
import '../src/index.css';

function App() {
  const form = document.querySelector('.form')
  const resu = [
    {name:"ali galal",
    id:2775,
    resu:"70%"},
    {name:"ahmed khalil",
    id:2776,
    resu:"60%"},
    {name:"sobhy fawzi",
    id:3030,
    resu:"98%"},
  ]

  const loo = []
  form.addEventListener('submit', e =>{
    e.preventDefault()
    const formData = new FormData(form);
    const seatNum = formData.get('seatNum');

    loo = resu.filter(stu => stu.id == seatNum)

  })
  
    return (
      <div className='container'>
        <Tasks loo={loo}/>
      </div>
    );
}

export default App;
