    {text:'happy',id:1}
    ])
  form.addEventListener('submit', e =>{
    e.preventDefault()
    const formData = new FormData(form);
    const seatNum = formData.get('seatNum');

    const updatedVars = [vars];
    updatedVars[0].id = seatNum;

    tastStat(updatedVars);
  })
  
    return (
      <div className='container'>
        <Tasks vasrs={vars}/>
      </div>
    );
}

export default App;
