import Boton from './Boton';

const Carde = (proms) => {
    const handleClick = () => {
        console.log(proms.para);
      };
      const addThings = () => {
        const addHereDiv = document.querySelector(`.addHere${proms.class}`);
        const newPara = document.createElement("p");
        newPara.textContent = proms.para;
        addHereDiv.appendChild(newPara);
      };
      
    return (
    <div className={`${proms.class} card`}>
        <img src={proms.img}/>
        <div className="text-info">
            <h1>{proms.name}</h1>
            <div className={`addHere${proms.class}`}>
                
            </div>
            <Boton bgc="red" text="Give" color="white" onClick={handleClick}/>
            <Boton bgc="yellow" text="ajouter" color="black"/>
            <Boton bgc="white" text="Follow" color="black" onClick={addThings}/>
        </div>
     </div>
    )
  }
  export default Carde
